-- Enum for api direction
CREATE TYPE api_direction AS ENUM ('OUTBOUND', 'INBOUND');

-- Units table (sem rede_id)
CREATE TABLE IF NOT EXISTS public.units (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  slug text NOT NULL UNIQUE,
  cidade text,
  estado text,
  telefone text,
  endereco text,
  latitude text,
  chave_api text, -- Chave criptografada
  chave_publica text, -- Chave não criptografada
  codigo_unidade text, -- unit_code
  nome text NOT NULL,
  cep text,
  complemento text,
  moeda text[], -- Array de texto para suportar múltiplas moedas
  imagens text[], -- Array de texto para URLs de imagens
  usarSistemaInternacional boolean,
  logo text,
  email text,
  longitude text,
  locale text,
  created_at timestamptz NOT NULL DEFAULT now(),
  updated_at timestamptz NOT NULL DEFAULT now()
);

-- Api Log table (sem referência a rede)
CREATE TABLE IF NOT EXISTS public.api_log (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  unidade_id uuid REFERENCES public.units(id) ON DELETE SET NULL,
  direction api_direction NOT NULL,
  method text NOT NULL,
  endpoint text NOT NULL,
  status_code int,
  latency_ms int,
  error text,
  request_hash text,
  created_at timestamptz NOT NULL DEFAULT now()
);

